    text-align: center;
    line-height: 34px;
    color: #fff;
    background: #392b13;
    border: none
}
.cstyle.cselect .jq-selectbox__trigger-arrow {
    display: inline-block;
    font: normal normal normal 14px/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    transform: translate(0, 0);
    opacity: 100;
    filter: alpha(opacity=10000);
    position: static;
    left: auto;
    top: auto;
    width: auto;
    height: auto;
    border: none;
    font-size: 18px
}
.cstyle.cselect .jq-selectbox__trigger-arrow.pull-left {
    margin-right: .3em
}
.cstyle.cselect .jq-selectbox__trigger-arrow.pull-right {
    margin-left: .3em
}
.cstyle.cselect .jq-selectbox__trigger-arrow:before {
    content: "\f078"
}
.cstyle.cselect .jq-selectbox__dropdown {
    text-align: left;
    border-top-right-radius: 0;
    border-top-left-radius: 0;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
    -webkit-box-shadow: none;
    box-shadow: none;
    text-transform: uppercase;
    font-family: 'Open Sans', sans-serif;
    border-color: #392b13
}
.cstyle.cselect.opened .jq-selectbox__trigger-arrow:before {
    content: "\f077"
}
.cstyle.ccheckbox {
    -webkit-box-shadow: none;
    box-shadow: none;
    position: relative;
    width: 14px;
    height: 14px;
    background: none;
    border: none
}
.cstyle.ccheckbox:after {
    display: inline-block;
    font: normal normal normal 14px/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    transform: translate(0, 0);
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    width: 100%;
    height: 100%;
    content: "\f096"
}
.cstyle.ccheckbox:after.pull-left {
    margin-right: .3em
}
.cstyle.ccheckbox:after.pull-right {
    margin-left: .3em
}
.cstyle.ccheckbox.checked:after {
    left: 1px;
    content: "\f046"
}
.cstyle.ccheckbox.size-2x {
    width: 30px;
    height: 24px
}
.cstyle.ccheckbox.size-2x:after {
    font-size: 2em
}
.cstyle.ccheckbox.size-2x.checked:after {
    left: 2px
}
